var Utils = require('../utils');
var _ = require('lodash-node');

// TODO core filters: self, toself, fromself, command (auto argv)

exports.filters = {
  text: function filterText(context, filterData) {
    var match = Utils.matchString(context.text, filterData);
    if (_.isObject(match)) {
      context.matches = match;
    }
    return match || false;
  },

  channel: function filterChannel(context, filterData) {
    var match = Utils.matchString(context.channel, filterData);
    if (_.isObject(match)) {
      context.matches = match;
    }
    return match || false;
  }
};
